CREATE TABLE tanks (
	id INT PRIMARY KEY AUTO_INCREMENT,
    model VARCHAR(50) NOT NULL,
    generation INT NOT NULL,
    tankType ENUM('light', 'medium', 'heavy', 'super heavy'),
    tankRole ENUM('assault','support','reconnaisance','cavalry','infantry','storage','tank destroyer','self-propelled guns'),
    country VARCHAR(20) NOT NULL,
    manufacturer VARCHAR(50) NOT NULL,
    weight DECIMAL(8,2),
    speed DECIMAL(8,2),
    fireRange DECIMAL(8,2),
    cannon DECIMAL(8,2),
    engine ENUM('diesel', 'turbine'),
    crew INT NOT NULL,
    unitPrice DECIMAL(8,2),
    dateManufactured DATE NOT NULL,
    image TEXT
);

